import React from "react";
import Container from "@/components/pages/common/Container/Container";
import Title from "@/components/pages/common/Title/Title";
import Description from "@/components/pages/common/Description/Description";
import Encore from "@/components/pages/Confirm/Components/Encore/Encore";
import ActionButton from "@/components/pages/common/ActionButton/ActionButton";
import styles from "@/components/pages/Confirm/ConfirmPage.module.scss";

const ConfirmPage = () => {
  return (
    <div className={styles.container}>
      <Title>Подтвердите ваш e-mail</Title>
      <Description>
        Игорь, на ваш E-mail отправлено письмо со ссылкой для подтверждения.
        Перейдите по ней, чтобы активировать вашу учетную запись и получить 7
        дней бесплатного доступа.{" "}
      </Description>
      <ActionButton>Перейти к почте</ActionButton>
    </div>
  );
};
export default ConfirmPage;
