import React from "react";
import { setEmail, setError } from "@/redux/store/login-page/login-pahe-slice";
import Input from "@/components/common/Input/Input";
import { useDispatch, useSelector } from "react-redux";

const LoginInputName = () => {
  const dispatch = useDispatch();
  const value = useSelector((state) => state.login.emailSignIn);
  const error = useSelector((state) => state.login.error);

  return (
    <Input
      error={error}
      errorMessage={"Возможно вы ошиблись в указании почтового сервиса"}
      type={"email"}
      name={"email"}
      placeholder={"Email"}
      value={value}
      onChange={(e) => {
        dispatch(setEmail(e.target.value));
        dispatch(setError(false));
      }}
    />
  );
};
export default LoginInputName;
