import React from "react";
import Button from "../../components/Button";
import styles from "./Confirm.module.scss";
import { useSelector } from "react-redux";
const Confirm = () => {
  const nameReg = useSelector((state) => state.auth.nameRegistration);
  return (
    <div className={styles.container}>
      <h1>Подтвердите ваш e-mail</h1>
      <h2>
        `${nameReg}, на ваш E-mail отправлено письмо со ссылкой для
        подтверждения. Перейдите по ней, чтобы активировать вашу учетную запись
        и получить 7 дней бесплатного доступа.`
      </h2>
      <div className={styles.mail_btnWrapper}>
        <Button>Перейти к почте</Button>
      </div>

      <h3>Мне не пришло письмо</h3>
    </div>
  );
};
export default Confirm;
