import * as React from "react";
import styles from "./Registration.module.scss";
import Socials from "../../components/Socials";
import Input from "../../components/Input";
import {
  setNameReg,
  setPasswordReg,
  setEmailReg,
  setPromoReg,
} from "../../Redux/Store/authSlice";
import Button from "../../components/Button";
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";
import { Link } from "react-router-dom";

const Registration = () => {
  const nameRegState = useSelector((state) => state.auth.nameRegistration);
  const emailRegState = useSelector((state) => state.auth.emailRegistration);
  const passwordRegState = useSelector(
    (state) => state.auth.passwordRegistration
  );
  const promoRegState = useSelector((state) => state.auth.promoRegistration);

  const [error, setError] = useState(false);
  const dispatch = useDispatch();
  const focusHandler = () => {
    setError(false);
  };

  return (
    <div className={styles.container}>
      <h1>Регистрация</h1>
      <h3>Зарегистрируйся и получи доступ к аналитике аккаунтов.</h3>
      <Socials />
      <span className={styles.orText}>или</span>
      <div className={styles.login_wrapper}>
        <Input
          focus={focusHandler}
          error={error}
          type={"name"}
          placeholder={"Имя"}
          value={nameRegState}
          setSlice={setNameReg}
        />
        <Input
          focus={focusHandler}
          error={error}
          type={"email"}
          placeholder={"Email"}
          value={emailRegState}
          setSlice={setEmailReg}
        />
        {error && (
          <div className={styles.errorText}>
            Возможно вы ошиблись в указании почтового сервиса
          </div>
        )}
        <Input
          value={passwordRegState}
          focus={focusHandler}
          error={error}
          type={"password"}
          placeholder={"Пароль"}
          setSlice={setPasswordReg}
        />
        <Input
          error={error}
          focus={focusHandler}
          type={"text"}
          placeholder={"Промокод"}
          value={promoRegState}
          setSlice={setPromoReg}
        />
      </div>
      <div className={styles.account_btnWrapper}>
        <Link to="/confirm">
          <Button change={() => {}}>Войти в аккаунт</Button>
        </Link>
      </div>
      <div className={styles.forgot}>
        Создавая аккаунт, я согласен с <a> условиями оферты</a>
      </div>
    </div>
  );
};
export default Registration;
