import React from "react";
import styles from "../Input/input.module.scss";
import { useDispatch, useSelector } from "react-redux";
import cn from "classnames";

const Input = ({ placeholder, type, focus, value, setSlice, success }) => {
  const dispatch = useDispatch();
  const errorState = useSelector((state) => state.auth.error);

  return (
    <input
      type={type}
      placeholder={placeholder}
      onFocus={() => focus()}
      value={value}
      className={cn(styles.input, {
        [styles.input_success]: success,
        [styles.input_error]: errorState,
      })}
      // className={
      //   errorState ? styles.error : success ? styles.success : styles.input
      // }
      onChange={(e) => dispatch(setSlice(e.target.value))}
    ></input>
  );
};
export default Input;
