import * as React from "react";
import styles from "../login/Login.module.scss";
import Socials from "../../components/Socials";
import Input from "../../components/Input";
import { setEmail, setPassword } from "../../Redux/Store/authSlice";
import Button from "../../components/Button";
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";

const Registration = () => {
  const emailState = useSelector((state) => state.auth.email);
  const passwordState = useSelector((state) => state.auth.password);
  const [error, setError] = useState(false);
  const [success, setSuccess] = useState(false);
  const dispatch = useDispatch();
  const focusHandler = () => {
    setError(false);
    setSuccess(false);
  };
  const submitFormHandler = () => {
    if (
      emailState === "example@example.com" &&
      passwordState === "password2021"
    ) {
      setSuccess(true);
      setTimeout(() => setSuccess(false), 3000);
      setTimeout(() => dispatch(setEmail("")), 3000);
      setTimeout(() => dispatch(setPassword("")), 3000);
    } else {
      setError(true);
      dispatch(setEmail(""));
      dispatch(setPassword(""));
    }
  };

  return (
    <div className={styles.container}>
      <h1>Регистрация</h1>
      <h3>Зарегистрируйся и получи доступ к аналитике аккаунтов.</h3>
      <Socials />
      <span className={styles.orText}>или</span>
      <div className={styles.login_wrapper}>
        <Input
          focus={focusHandler}
          success={success}
          error={error}
          type={"email"}
          placeholder={"Email"}
          value={emailState}
          setSlice={setEmail}
        />
        <Input
          value={passwordState}
          success={success}
          focus={focusHandler}
          error={error}
          type={"password"}
          placeholder={"Пароль"}
          setSlice={setPassword}
        />
      </div>
      {error && (
        <div className={styles.errorText}>Неверный еmail или пароль</div>
      )}
      {success && (
        <div className={styles.successText}>Email и пароль верны!</div>
      )}
      <div className={styles.account_btnWrapper}>
        <Button change={submitFormHandler}>Войти в аккаунт</Button>
      </div>
      <div className={styles.forgot}>
          Создавая аккаунт, я согласен с <a> условиями оферты</a>
      </div>
    </div>
  );
};
export default Registration;
