import React from "react";
import styles from "../Input/input.module.scss";
import { useDispatch, useSelector } from "react-redux";
import { setError } from "../../Redux/Store/authSlice";

const Input = ({ placeholder, type, focus, value, setSlice, success }) => {
  const dispatch = useDispatch();
  const errorState = useSelector((state) => state.auth.error);
  const errorStateReg = useSelector((state) => state.auth.errorRegistration);
  const isValidEmail = (email) => {
    return /\S+@\S+\.\S+/.test(email);
  };

  // const onChangeInput = (e) => {
  //   if (isValidEmail(e.target.value)) {
  //     dispatch(setSlice(e.target.value));
  //   } else {
  //     dispatch(setError(true));
  //   }
  // };

  return (
    <input
      type={type}
      placeholder={placeholder}
      onFocus={() => focus()}
      value={value}
      className={
        errorState
          ? styles.error
          : errorStateReg
          ? styles.error
          : success
          ? styles.success
          : styles.input
      }
      onChange={(e) => dispatch(setSlice(e.target.value))}
    ></input>
  );
};
export default Input;
