import React from "react";
import styles from "./Registration.module.scss";
import Socials from "../../components/Socials";
import Input from "../../components/Input";
import { useNavigate } from "react-router-dom";
import {
  setNameReg,
  setPasswordReg,
  setEmailReg,
  setPromoReg,
  setError,
} from "../../Redux/Store/authSlice";
import Button from "../../components/Button";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";

const Registration = () => {
  const navigate = useNavigate();
  const nameRegState = useSelector((state) => state.auth.nameRegistration);
  const emailRegState = useSelector((state) => state.auth.emailRegistration);
  const passwordRegState = useSelector(
    (state) => state.auth.passwordRegistration
  );

  const promoRegState = useSelector((state) => state.auth.promoRegistration);
  const errorState = useSelector((state) => state.auth.error);
  const dispatch = useDispatch();
  const focusHandler = () => {
    dispatch(setError(false));
  };
  const isValidEmail = (email) => {
    return /\S+@\S+\.\S+/.test(email);
  };
  const createAccountHandler = () => {
    if (!isValidEmail(emailRegState) || emailRegState.length > 19) {
      dispatch(setError(true));
    } else {
      navigate("/session-timed-out");
    }
  };
  return (
    <div className={styles.container}>
      <h1>Регистрация</h1>
      <h3>Зарегистрируйся и получи доступ к аналитике аккаунтов.</h3>
      <Socials />
      <span className={styles.orText}>или</span>
      <div className={styles.login_wrapper}>
        <Input
          focus={focusHandler}
          type={"name"}
          placeholder={"Имя"}
          value={nameRegState}
          setSlice={setNameReg}
        />
        <Input
          focus={focusHandler}
          error={errorState.email}
          type={"email"}
          placeholder={"Email"}
          value={emailRegState}
          setSlice={setEmailReg}
        />
        {errorState && (
          <div className={styles.errorText}>
            Возможно вы ошиблись в указании почтового сервиса
          </div>
        )}
        <Input
          value={passwordRegState}
          focus={focusHandler}
          type={"password"}
          placeholder={"Пароль"}
          setSlice={setPasswordReg}
        />
        <Input
          focus={focusHandler}
          type={"text"}
          placeholder={"Промокод"}
          value={promoRegState}
          setSlice={setPromoReg}
        />
      </div>
      <div className={styles.account_btnWrapper}>
        <Button click={() => createAccountHandler()}>Создать аккаунт</Button>
      </div>
      <div className={styles.forgot}>
        Создавая аккаунт, я согласен с <a> условиями оферты</a>
      </div>
    </div>
  );
};
export default Registration;
