import React, { useEffect, useState } from "react";
import Button from "../../components/Button";
import styles from "./notMail.module.scss";
import Input from "../../components/Input";
import { Link } from "react-router-dom";
import { Oval } from "react-loader-spinner";

const Confirm = () => {
  const [isLoad, setIsLoad] = useState(false);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(false);
  const onfocusHandler = () => {
    setError(false);
  };
  const sendHandler = () => {
    setIsLoad(true);
    setTimeout(() => setIsLoad(false), 4000);
    setTimeout(() => setSuccess(true), 4000);
  };
  useEffect(() => {
    return () => setSuccess(false);
  }, []);
  return (
    <div className={styles.container}>
      <h1>Мне не пришло письмо</h1>
      <h2>
        Письмо может прийти с задержкой в 5-10 минут.
        <br />
        Также проверьте разные папки почтового ящика (актуально для gmail.com) и
        папку "Спам".Если письмо все же не пришло, повторите попытку или
        напишите об этом в тех.поддержку <a>support@livedune.ru</a> и мы
        активируем ваш аккаунт.
      </h2>
      <div className={styles.login_wrapper}>
        <Input focus={onfocusHandler} />
      </div>
      {error && (
        <div className={styles.message_error}>
          Обязательное поле для заполнения
        </div>
      )}
      {isLoad ? (
        <div className={styles.mail_btnWrapper}>
          {" "}
          <Button>
            <div className={styles.mail_btnWrapper_loader}>
              <Oval
                height={40}
                width={40}
                color="#FFFFFFFF"
                wrapperStyle={{}}
                wrapperClass=""
                visible={true}
                ariaLabel="oval-loading"
                strokeWidth={1}
                strokeWidthSecondary={1}
              />
            </div>
          </Button>
        </div>
      ) : (
        <div onClick={sendHandler} className={styles.mail_btnWrapper}>
          <Button success={success}>Отправить заново</Button>{" "}
        </div>
      )}

      <Link to="/confirm">
        <button className={styles.btn_cancel}>Отменить</button>
      </Link>
    </div>
  );
};
export default Confirm;
