import React, { useState } from "react";
import Button from "../../components/Button";
import styles from "./notMail.module.scss";
import Input from "../../components/Input";
import { Link } from "react-router-dom";
import { Oval } from "react-loader-spinner";

const Confirm = () => {
  const [isLoad, setIsLoad] = useState(false);
  return (
    <div className={styles.container}>
      <h1>Мне не пришло письмо</h1>
      <h2>
        Письмо может прийти с задержкой в 5-10 минут.
        <br />
        Также проверьте разные папки почтового ящика (актуально для gmail.com) и
        папку "Спам".Если письмо все же не пришло, повторите попытку или
        напишите об этом в тех.поддержку <a>support@livedune.ru</a> и мы
        активируем ваш аккаунт.
      </h2>
      <div className={styles.login_wrapper}>
        <Input />
      </div>
      {isLoad ? (
        <div className={styles.mail_btnWrapper}>
          {" "}
          <Oval
            height={80}
            width={80}
            color="#4fa94d"
            wrapperStyle={{}}
            wrapperClass=""
            visible={true}
            ariaLabel="oval-loading"
            secondaryColor="#4fa94d"
            strokeWidth={2}
            strokeWidthSecondary={2}
          />
        </div>
      ) : (
        <div className={styles.mail_btnWrapper}>
          <Button>Отправить заново</Button>{" "}
        </div>
      )}

      <Link to="/confirm">
        <button className={styles.btn_cancel}>Отменить</button>
      </Link>
    </div>
  );
};
export default Confirm;
